INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Casa Loma', 9, 43.678313, -79.409759, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'Casa Loma is a Gothic Revival style mansion and garden in midtown Toronto, Ontario, Canada, that is now a historic house museum and landmark. It was constructed from 1911 to 1914 as a residence for financier Sir Henry Pellatt.', 'https://casaloma.ca/');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('CN Tower', 9, 43.642038, -79.38695899999999, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'The CN Tower is a 553.3 m-high concrete communications and observation tower located in Downtown Toronto, Ontario, Canada. Built on the former Railway Lands, it was completed in 1976. Its name CN originally referred to Canadian National, the railway company that built the tower.', 'https://www.cntower.ca/intro.html');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Distellery District', 9, 43.650201, -79.35948499999999, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'The pedestrian-only Distillery District, set in quaint 19th century buildings that once housed a large whiskey distillery, draws crowds to its cobblestone streets lined with hip indie restaurants, bars and boutiques. Art lovers come for the galleries, outdoor sculptures and dance, music and stage performances at the areas several theatres. In December, the annual Toronto Christmas Market takes over the streets.', 'https://www.thedistillerydistrict.com/');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Museum of Illusions', 9, 43.65027, -79.369556, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'Small museum with optical illusions & holograms for selfies, plus logic games & a vortex tunnel.', 'https://www.museumofillusions.com/');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Ripleys Aquarium', 9, 43.641993, -79.386788, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'Ripleys Aquarium of Canada is a public aquarium in Toronto, Ontario, Canada. The aquarium is one of three aquariums owned and operated by Ripley Entertainment. It is located in downtown Toronto, just southeast of the CN Tower.', 'https://www.ripleyaquariums.com/canada/');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Royal Ontario Museum', 9, 43.668621, -79.394856, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'The Royal Ontario Museum is a museum of art, world culture and natural history in Toronto, Ontario, Canada. It is one of the largest museums in North America and the largest in Canada. It attracts more than one million visitors every year, making the ROM the most-visited museum in Canada.', 'https://www.rom.on.ca/en');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Scarborough Bluffs', 9, 43.706162, -79.232093, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'The Scarborough Bluffs, also known as The Bluffs, is an escarpment in the Scarborough district of Toronto, Ontario, Canada. There are nine parks along the bluffs, with Bluffers Park being the only one with a beach.', 'N/A');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('St.Lawrence Market', 9, 43.648821999999996, -79.372016, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'The St. Lawrence Market South building is a major public market building in Toronto, Ontario, Canada. It is located on the southwest corner of Front and Lower Jarvis Streets. Along with the St. Lawrence Market North and St. Lawrence Hall, it comprises the St. Lawrence Market complex.', 'http://www.stlawrencemarket.com/');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Toronto Harbour Tours', 9, 43.637762, -79.382795, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'A cruise with Toronto Harbour Tours lets you enjoy the scenic Toronto skyline, and gives you a close up look at the beautiful Toronto Islands and waterfront.', 'https://harbourtourstoronto.ca/');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Toronto Zoo', 9, 43.820229, -79.17924599999999, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'Our Toronto Zoo - Connecting people, animals and conservation science to fight extinction.', 'https://www.torontozoo.com/');
INSERT INTO locations (name, map_id, lat, long, user_id, picture_url, description, website) VALUES ('Union Station', 9, 43.645613, -79.38051, 3, 'https://thumbnail.imgbin.com/0/9/7/imgbin-south-america-canada-organization-of-american-states-continent-map-canada-6PGHgTE8gvVsvPLhK5tpXfn2f_t.jpg', 'Union Station is a major railway station and intermodal transportation hub in Toronto, Ontario, Canada.', 'https://torontounion.ca/');
